mapper.update:
update tbl_item
set item_name = #{itemName},
    item_price = #{itemPrice},
    item_stock = #{itemStock},
    item_content = #{itemContent}
where id = #{id}

update tbl_item
set item_state = #{itemState}
where id = #{id}

update tbl_item
set item_view_count = item_view_count + 1
where id = #{id}

mapper.select:
select id, item_store_id, item_category_id, item_subcategory_id, item_name, item_type, item_stock, item_price, item_delivery_fee, item_content, item_state, item_view_count, created_datetime, updated_datetime
from tbl_item
where id = #{id}
and item_state = 'active';

select id, option_item_id, option_name, option_detail, option_price, option_stock
from tbl_item_option
where option_item_id = #{optionItemId}

select i.id, i.item_store_id, i.item_category_id, i.item_subcategory_id, i.item_name, i.item_type, i.item_stock, i.item_price, i.item_delivery_fee, i.item_content, i.item_state, i.item_view_count, i.created_datetime, i.updated_datetime,
s.id as storeId, s.store_name as storeName from tbl_item i left join tbl_category c on i.item_category_id = c.id left join tbl_sub_category sc on i.item_subcategory_id = sc.id join tbl_store s on i.item_store_id = s.id
i.item_subcategory_id = #{subCategoryId}
i.item_category_id = #{categoryId}
and i.id != #{thisItemId}
and i.item_state = 'active'
order by i.created_datetime desc limit 10
