mapper.select:
select id, store_market_id, store_owner_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_market_id = #{storeMarketId}

select id, store_market_id, store_owner_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where id = #{id}

select id, store_market_id, store_owner_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_name = #{storeName}

select id, store_market_id, store_owner_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_owner_id = #{storeOwnerId}

mapper.update:
update tbl_store
set store_state = #{storeState}
where id = #{id}

update tbl_store
set store_is_confirmed = #{storeIsConfirmed}
where id = #{id}
