mapper.select:
select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_market_id = #{marketId}

select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where id = #{id}

select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_name = #{storeName}

select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_owner_id = #{ownerId}

select distinct s.id, s.store_name, s.store_address, s.store_category_id, s.store_state, s.store_market_id, s.store_intro,
f.file_name as fileName, f.file_origin_name as fileOriginName, f.file_saved_path as fileSavePath from tbl_store s left join tbl_file_store fs on s.id = fs.store_id left join tbl_file f on f.id = fs.id
limit #{criteria.count} offset #{criteria.offset}

mapper.update:
update tbl_store
set store_state = #{state}
where id = #{id}

update tbl_store
set store_state = 'close'
where id = #{id}
