mapper.select:
select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_market_id = #{marketId}

select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where id = #{id}

select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_name = #{storeName}

select id, store_market_id, store_owner_id, store_category_id, store_name, store_intro, store_address, store_score, store_state, store_is_confirmed, created_datetime, updated_datetime
from tbl_store
where store_owner_id = #{ownerId}

mapper.update:
update tbl_store
set store_state = #{state}
where id = #{id}

update tbl_store
set store_state = 'close'
where id = #{id}
